mapper.select:
/*后台展示博客*/
       select b.id, b.published, b.flag, b.title, b.content, b.type_id,
       b.tag_ids, b.first_picture, b.description, b.recommend,
       b.share_statement, b.appreciation, b.commentabled
       from blog b
       where  b.id = #{id};

mapper.update:
update blog set
views = views + 1
where id =#{id}
